// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CarbonCredit is ERC20, Ownable {
    constructor() ERC20("CarbonCreditToken", "CCT") {}

    /**
     * @dev Mints carbon credits to a specified address.
     * @param to Address to receive the tokens.
     * @param amount Number of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Burns carbon credits from a specified address.
     * @param from Address holding the tokens to be burned.
     * @param amount Number of tokens to burn.
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }
}

contracts/ICMHandler.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IICM {
    function sendMessage(address target, bytes calldata message) external;
}

interface ICarbonCredit {
    function burn(address from, uint256 amount) external;
    function mint(address to, uint256 amount) external;
}

contract ICMHandler {
    address public icmRouter; // ICM Router address
    ICarbonCredit public carbonCredit; // Reference to CarbonCredit contract

    event CrossChainTransfer(address indexed from, address indexed to, uint256 amount, uint256 chainId);

    constructor(address _icmRouter, address _carbonCredit) {
        icmRouter = _icmRouter;
        carbonCredit = ICarbonCredit(_carbonCredit); // Cast the address as the CarbonCredit contract
    }

    /**
     * @dev Transfers carbon credits across Avalanche chains using ICM.
     * @param to Recipient address on the target chain.
     * @param amount Number of tokens to transfer.
     * @param chainId ID of the target chain.
     */
    function transferCredit(address to, uint256 amount, uint256 chainId) external {
        // Burn tokens on the source chain
        carbonCredit.burn(msg.sender, amount);

        // Encode the transfer message
        bytes memory message = abi.encode(to, amount, chainId);

        // Send the message via ICM
        IICM(icmRouter).sendMessage(address(this), message);

        emit CrossChainTransfer(msg.sender, to, amount, chainId);
    }

    /**
     * @dev Handles incoming cross-chain messages to mint tokens on the target chain.
     * @param from Source address on the original chain.
     * @param to Recipient address on the current chain.
     * @param amount Number of tokens to mint.
     */
    function handleCrossChainMessage(address from, address to, uint256 amount) external {
        require(msg.sender == icmRouter, "Unauthorized caller");
        carbonCredit.mint(to, amount);
    }
}

contracts/Marketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CarbonCredit.sol";
import "./PriceOracle.sol";

contract Marketplace {
    struct Listing {
        address seller;
        uint256 amount;
        uint256 price; // Price per token in wei
    }

    CarbonCredit public carbonCredit;
    PriceOracle public priceOracle;

    mapping(uint256 => Listing) public listings;
    uint256 public nextListingId;

    event Listed(address indexed seller, uint256 indexed id, uint256 amount, uint256 price);
    event Sold(address indexed buyer, uint256 indexed id, uint256 amount, uint256 price);

    constructor(address _carbonCredit, address _priceOracle) {
        carbonCredit = CarbonCredit(_carbonCredit);
        priceOracle = PriceOracle(_priceOracle);
    }

    /**
     * @dev Allows a seller to list carbon credits for sale using the latest oracle price.
     * @param amount Number of tokens to sell.
     */
    function listCredit(uint256 amount) external {
        uint256 currentPrice = priceOracle.getPrice();
        require(carbonCredit.balanceOf(msg.sender) >= amount, "Insufficient balance");

        // Transfer tokens to the contract for escrow
        carbonCredit.transferFrom(msg.sender, address(this), amount);

        listings[nextListingId] = Listing(msg.sender, amount, currentPrice);
        emit Listed(msg.sender, nextListingId, amount, currentPrice);
        nextListingId++;
    }

    /**
     * @dev Allows a buyer to purchase listed carbon credits.
     * @param id Listing ID to purchase from.
     */
    function buyCredit(uint256 id) external payable {
        Listing storage listing = listings[id];
        require(listing.amount > 0, "Listing does not exist");
        require(msg.value == listing.amount * listing.price, "Incorrect payment amount");

        // Transfer tokens to the buyer and ETH to the seller
        carbonCredit.transfer(msg.sender, listing.amount);
        payable(listing.seller).transfer(msg.value);

        emit Sold(msg.sender, id, listing.amount, listing.price);
        delete listings[id];
    }
}

contracts/PriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IOracle {
    function getLatestPrice() external view returns (uint256);
}

contract PriceOracle {
    uint256 public currentPrice;
    address public oracle;

    event PriceUpdated(uint256 oldPrice, uint256 newPrice);

    constructor(address _oracle) {
        oracle = _oracle;
    }

    /**
     * @dev Updates the current price using the external oracle.
     */
    function updatePrice() external {
        uint256 newPrice = IOracle(oracle).getLatestPrice();
        require(newPrice > 0, "Invalid price from oracle");

        emit PriceUpdated(currentPrice, newPrice);
        currentPrice = newPrice;
    }

    /**
     * @dev Returns the current price.
     */
    function getPrice() external view returns (uint256) {
        return currentPrice;
    }
}

